if self._es is None and self._cdf is not None:
    if self.gcol in self._cdf.columns:
        gser = self._cdf[self.gcol]
        gser = explode_json_series(gser)
        if len(gser) > 0:
            self._es = gser
    if self._es is None:
        self._es = pd.Series(
            np.nan,
            index=self._cdf.index,
            name=self.gcol,
        )

with self.get_conn() as conn, conn.cursor() as curs:
    if params is None:
        curs.execute(query)
    else:
        curs.execute(query, params)
    df = pd.DataFrame(curs.fetchall(), columns=[desc[0] for desc in curs.description])

with self.get_conn() as conn, conn.cursor() as curs:
    sql = f"INSERT INTO {table_name} ({fields}) VALUES " + ",".join(
        curs.mogrify(
            f"({template})",
            [str(row[col]) for col in df.columns],
        ).decode("utf-8")
        for row in df.to_records()
    )
    curs.execute(sql)

if output and not handle_line_fn(output.strip()):
    process.kill()
    break

if text_obj is not None and isinstance(text_obj, str) and len(text_obj.strip()) > 0:
    text_obj = dk_annots.AnnotatedText(
        text_obj,
        annots_metadata=self.metadata,
    )
